import React, { useState, useEffect } from "react";
import { Navigation } from "./components/navigation";
import { Header } from "./components/header";
import { Features } from "./components/features";
import { About } from "./components/about";
import { Services } from "./components/services";
import { Gallery } from "./components/gallery";
import { Testimonials } from "./components/testimonials";
import { Team } from "./components/Team";
import { Contact } from "./components/contact";
import JsonData from "./data/data.json";
import SmoothScroll from "smooth-scroll";
import "./App.css";
import { Routes, Route } from "react-router-dom"; // ไม่ต้องใช้ <Router>

// import App2 from "./components/Test";

// ใช้ SmoothScroll
export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
});

const App = () => {
  const [landingPageData, setLandingPageData] = useState({});

  useEffect(() => {
    // โหลดข้อมูล JSON
    setLandingPageData(JsonData);

    // Intersection Observer สำหรับทุก `.animate-on-scroll`
    const elements = document.querySelectorAll(".animate-on-scroll");
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("in-view");
          }
        });
      },
      {
        threshold: 0.1, // เริ่มทำงานเมื่อ 10% ขององค์ประกอบเข้ามาใน viewport
      }
    );

    // สังเกตการณ์ทุกองค์ประกอบ
    elements.forEach((el) => observer.observe(el));

    // Cleanup observer เมื่อ component ถูกทำลาย
    return () => {
      elements.forEach((el) => observer.unobserve(el));
    };
  }, []);

  return (
    <Router>
      <Navigation />
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Header data={landingPageData.Header} />
              <Features data={landingPageData.Features} />
              <About data={landingPageData.About} />
              <Services data={landingPageData.Services} />
              <Gallery data={landingPageData.Gallery} />
              <Testimonials data={landingPageData.Testimonials} />
              <Team data={landingPageData.Team} />
              <Contact data={landingPageData.Contact} />
            </>
          }
        />
        <Route
          path="/about"
          element={<About data={landingPageData.About} />}
        />
        <Route
          path="/services"
          element={<Services data={landingPageData.Services} />}
        />
        <Route
          path="/team"
          element={<Team data={landingPageData.Team} />}
        />
        <Route
          path="/contact"
          element={<Contact data={landingPageData.Contact} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
