import React from "react";

export const Features = (props) => {
  return (
    <div id="features" className="text-center features">
    <div className="container">
      <div className="section-title">
        <h2>Features (คุณสมบัติ)</h2>
      </div>
      <div className="row">
        {props.data ? props.data.map((d, i) => {
              return (
                <div key={`${d.title}-${i}`} className="card col-12 col-md-6 col-lg-2 m-4  " // จัดการขนาดตามหน้าจอ
                  style={{ marginBottom: "" }} // เพิ่มระยะห่างระหว่างการ์ด
                >
                  {/* การเเสดง icon */}
                  <i style={{textAlign:"center"}} className={d.icon}></i>
                  {/* <img src="..." className="card-img-top" alt="..."></img> */}
                  <div className="card-body">
                    <h3 className="card-title">{d.title}</h3>
                    <p className="card-text">{d.text}</p>
                    {/* <a href="#" className="btn btn-primary">
                      Go somewhere
                    </a> */}
                  </div>
                </div>
              );
            })
          : "Loading..."}
      </div>
    </div>
  </div>
    // <div className="card container" style={{width:"100px"}}>
    //   <img src="" className="card-img-top" alt="" />
    //   <div className="card-body">
    //     <h5 className="card-title">ssss</h5>
    //     <p className="card-text">sssssss</p>
    //     <a className="btn btn-primary">xxxx</a>
    //   </div>

    // </div>
  );
};

//  ----โค้ดเดิมที่ก่อนจะนำไปเเก้ ----
