import React from "react";
import logo from "../image/S__8675335.jpg"; //  this JS file uses this image


export const Navigation = (props) => {
  const handleNavLinkClick = () => {
    const navbarCollapse = document.getElementById("navbarNav");
    if (navbarCollapse.classList.contains("show")) {
      const collapse = new bootstrap.Collapse(navbarCollapse);
      collapse.hide();
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-body-tertiary fixed-top">
      <div className="container-fluid">
        <a className="navbar-brand" href="#page-top">
          <img src={logo} alt="Logo" style={{ height: "40px", width: "155px" }} />
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto">
            {["features", "about", "services", "testimonials", "team", "contact"].map(
              (section, index) => (
                <li className="nav-item" key={index}>
                  <a
                    className="nav-link"
                    href={`#${section}`}
                    onClick={handleNavLinkClick} // เพิ่ม onClick เพื่อปิดเมนู
                  >
                    {section.charAt(0).toUpperCase() + section.slice(1)}
                  </a>
                </li>
              )
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};
