import React from "react";

export const About = (props) => {

  useEffect(() => {
    const elements = document.querySelectorAll(".animate-on-scroll");
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("in-view");
          }
        });
      },
      {
        threshold: 0.1, // เริ่มทำงานเมื่อ 10% ขององค์ประกอบเข้ามาใน viewport
      }
    );

    elements.forEach((el) => observer.observe(el));

    // Cleanup observer
    return () => {
      elements.forEach((el) => observer.unobserve(el));
    };
  }, []);


  return (
    <div id="about">
    <div className="container">
      <div className="row">
        <div className="col-xs-12 col-md-6">
          <img
            src="img/lightsStage.jpg"
            className="img-responsive"
            alt=""
          />
        </div>
        <div className="col-xs-12 col-md-6">
          <div className="about-text">
            <h2 className="slide-in">About Us (เราคืออะไร)</h2>
            <p className="text-secondary fade-in">
              {props.data ? props.data.paragraph : "loading..."}
            </p>
            <h3 className="slide-in">Why Choose Us? (ทำไมต้องเลือกเรา)</h3>
            <div className="list-style">
              <div className="col-lg-6 col-sm-6 col-xs-12">
                <ul>
                  {props.data
                    ? props.data.Why.map((d, i) => (
                        <li key={`${d}-${i}`} className="fade-in">{d}</li>
                      ))
                    : "loading"}
                </ul>
              </div>
              <div className="col-lg-6 col-sm-6 col-xs-12">
                <ul>
                  {props.data
                    ? props.data.Why2.map((d, i) => (
                        <li key={`${d}-${i}`} className="fade-in">{d}</li>
                      ))
                    : "loading"}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  
  );
};
