import React from "react";
import { useNavigate } from "react-router-dom";

export const Services = (props) => {
  const navigate = useNavigate();

  return (
    <div id="services" className="text-center">
      <div className="container">
        <div className="section-title">
          <h2>Our Services(บริการของเรา)</h2>
          <p>
            เรามอบประสบการณ์ความบันเทิงที่ครบครันด้วยบริการให้เช่าเครื่องเสียงคุณภาพสูง
            พร้อมอุปกรณ์สำหรับจัดงานและทีมงานมืออาชีพ
            ที่จะช่วยสร้างความประทับใจในทุกโอกาสพิเศษของคุณ
          </p>
        </div>
        <div className="row">
          {props.data
            ? props.data.map((d, i) => (
                <div
                  key={`${d.name}-${i}`}
                  className="col-md-3 d-flex align-items-stretch"
                >
                  <div
                    className="card shadow-sm m-2 d-flex flex-column"
                    style={{ minHeight: "400px" }}
                  >
                    <img
                      src={d.img}
                      alt={d.name}
                      className="card-img-top"
                      style={{
                        height: "200px",
                        objectFit: "cover",
                      }}
                    />
                    <div className="card-body d-flex flex-column">
                      <h3 className="card-title">{d.name}</h3>
                      <p className="card-text">{d.text}</p>
                      <button
                        onClick={() => navigate("/detailsSV")}
                        className="btn btn-primary mt-auto" // ดันปุ่มให้อยู่ล่างสุด
                      >
                        Go somewhere
                      </button>
                    </div>
                  </div>
                </div>
              ))
            : "loading"}
        </div>
      </div>
    </div>
  );
};
